import { useState,useEffect } from "react";
import "./demo.css";
const {ethers} = require("ethers");

function App() {

  const [examcode, setexamcode] = useState("");
  const [totalmarks, settotalmarks] = useState();
  const [requiremarks, setrequiremarks] = useState();
  const [studentid, setstudentid] = useState("");

  const handleexamcode = (e) =>{
    setexamcode(e.target.value);
  }
  const handletotalmarks = (e) =>{
    settotalmarks(e.target.value);
  }
  const handlerequiremarks = (e) =>{
    setrequiremarks(e.target.value);
  }
  const handlestudentid = (e) =>{
    setstudentid(e.target.value);
  }

  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const signer = provider.getSigner();
  const contractadress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
  const abi = [
    {
      "inputs": [],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "_studentID",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "_exam_code",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "bool",
          "name": "_isMarksAssign",
          "type": "bool"
        },
        {
          "indexed": false,
          "internalType": "uint256",
          "name": "_studentsMarks",
          "type": "uint256"
        },
        {
          "indexed": false,
          "internalType": "bool",
          "name": "studentsExamResult",
          "type": "bool"
        }
      ],
      "name": "showDetails",
      "type": "event"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "_exam_code",
          "type": "string"
        },
        {
          "internalType": "uint256",
          "name": "_total_marks",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "_require_marks",
          "type": "uint256"
        }
      ],
      "name": "add_exam",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "_studentID",
          "type": "string"
        }
      ],
      "name": "add_students",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "_studentID",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "_exam_code",
          "type": "string"
        },
        {
          "internalType": "uint256",
          "name": "_assigned_marks",
          "type": "uint256"
        }
      ],
      "name": "assign_marks",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "_studentID",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "_exam_code",
          "type": "string"
        }
      ],
      "name": "getDetails",
      "outputs": [
        {
          "internalType": "string",
          "name": "studentid",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "examcode",
          "type": "string"
        },
        {
          "internalType": "bool",
          "name": "studentMarks",
          "type": "bool"
        },
        {
          "internalType": "uint256",
          "name": "obtainedmarks",
          "type": "uint256"
        },
        {
          "internalType": "bool",
          "name": "Result",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }
  ];

  useEffect(() => {
    const requestAccounts = async () => {
      await provider.send("eth_requestAccounts", []);
    }

    requestAccounts()
      .catch(console.error)

  }, []);

  const contract = new ethers.Contract(contractadress, abi, signer);

  const addexam = async(e) =>{
    e.preventDefault();
    const addexamdetails = await contract.add_exam(examcode,parseInt(totalmarks),parseInt(requiremarks));
    console.log("exam added ",examcode);
    console.log(totalmarks);
    console.log(requiremarks);
  }

  const addstudent = async(e) =>{
    e.preventDefault();
  }

  return (
    <>
    <div class="main-block">
      <div class="left-part">
        <img src="https://th.bing.com/th/id/OIP.wWRjqxDJcLTOmb-hgPMZOwHaBh?pid=ImgDet&rs=1"></img>
      </div>
    <div className="rightpart">
      <form onSubmit={addexam}>
        <h1>Marks Management System</h1>
        <div class="info">
          <h3 className="formheading">Add Exam Information</h3>
          <input class="fname" type="text" name="examcode" value={examcode} onChange={handleexamcode} placeholder="Exam Code" />
          <input type="number" name="tmarks" value={totalmarks} placeholder="Total Marks" onChange={handletotalmarks} />
          <input type="number" name="rmarks" value={requiremarks} placeholder="Require Marks" onChange={handlerequiremarks} />
        </div>
        <button type="submit" >Add Exam Information</button>
      </form>
      <form onSubmit={addstudent}>
          <h3 className="addstudent">Add Students</h3>
          <input class="fname" type="text" name="examcode" value={studentid} onChange={handlestudentid} placeholder="Students ID" />
        <button type="submit" href="/">Add Student</button>
      </form>
      </div>
    </div>
    <div className="addstudentmarks">
    <form action="/">
      <h1>Assign marks</h1>
      <div class="info">
        <h3 className="formheading">Assign Marks</h3>
        <input class="fname" type="text" name="examcode" placeholder="Student Id" />
        <input type="text" name="tmarks" placeholder="Exam Code" />
        <input type="text" name="rmarks" placeholder="Assigned Marks" />
      </div>
      <button type="submit" href="/">Add Marks</button>
    </form>
    </div>
    </>
  );
}
export default App;
